mapper.select:
select lead_id , reg_chnl_cd , con_type_cd , pros_id , pros_nm , cell_ph_no , cell_ph_tno , cell_ph_pno , eml_id , eml_domain , brand_cd , last_upd
from (select (rank() over(order by a.last_upd desc, b.pros_nm)) rownums , a.lead_id , a.reg_chnl_cd , a.con_type_cd , b.pros_id , b.pros_nm , b.cell_ph_no , b.cell_ph_tno , b.cell_ph_pno , b.eml_id , b.eml_domain , a.brand_cd , a.last_upd
from lead a, prospect b where a.pros_id = b.pros_id
) where rownums <= #{pageNum} * #{amount} and rownums > (#{pageNum}-1) * #{amount}
