mapper.select:
select s.brand_cd as brand_cd , s.fra_cd as fra_cd , s.str_cd as str_cd , s.str_nm as str_nm , s.str_rep_nm as str_rep_nm , s.ph_area_no as ph_area_no , s.ph_tno as ph_tno , s.ph_cno as ph_cno , s.str_addr as str_addr , s.str_addr_dtl as str_addr_dtl from ( select rownum rn , b.brand_cd as brand_cd , b.fra_cd as fra_cd , s.str_cd as str_cd , s.str_nm as str_nm , s.str_rep_nm as str_rep_nm , s.ph_area_no as ph_area_no , s.ph_tno as ph_tno , s.ph_cno as ph_cno , s.str_addr as str_addr , s.str_addr_dtl as str_addr_dtl
from franchise f , brand b , store s where b.brand_cd = s.brand_cd and f.fra_cd = b.fra_cd
and rownum <= #{pageNum} * #{amount} ) s where rn > (#{pageNum} -1) * #{amount}

mapper.insert:
	insert into 
     store 
	     (
	       str_cd
	     , del_yn
	     , created_by
	     , created
	     , last_upd_by
	     , last_upd
	     , str_nm
	     , str_rep_nm
	     , str_zipcd
	     , str_addr
	     , str_addr_dtl
	     , ph_area_no
	     , ph_tno
	     , ph_cno
	     , brand_cd
	     )
    values 
    	 ( 
    	   #{str_cd}
    	 , '0'
    	 , 'user03'
         , sysdate
         , 'user03'
         , sysdate
         , #{str_nm}
         , #{str_rep_nm}
         , #{str_zipcd}
         , #{str_addr}
         , #{str_addr_dtl}
         , #{ph_area_no}
         , #{ph_tno}
         , #{ph_cno}
         , #{brand_cd}
         )

mapper.update:
	update store
	   set str_cd = #{str_cd}
	   	 , str_nm = #{str_nm}
	     , str_rep_nm = #{str_rep_nm}
	     , str_zipcd = #{str_zipcd}
	     , str_addr = #{str_addr}
	     , str_addr_dtl = #{str_addr_dtl}
	     , ph_area_no = #{ph_area_no}
	     , ph_tno = #{ph_tno}
	     , ph_cno = #{ph_cno}
	     , brand_cd = #{brand_cd}
         , last_upd = sysdate
     where str_cd = #{str_cd}
